import React, { useState, useEffect, useRef } from 'react';
import './RotatingContainers.css';

const RotatingContainers = () => {
  const containerRef = useRef(null);
  const [containerWidth, setContainerWidth] = useState(0);
  const [scrollLeft, setScrollLeft] = useState(0);

  useEffect(() => {
    const container = containerRef.current;
    if (container) {
      const width = container.offsetWidth;
      setContainerWidth(width);
    }
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setScrollLeft((prevScrollLeft) => {
        const nextScrollLeft = prevScrollLeft + containerWidth;
        containerRef.current.scrollTo({
          left: nextScrollLeft,
          behavior: 'smooth',
        });
        return nextScrollLeft;
      });
    }, 3000);

    return () => clearInterval(interval);
  }, [containerWidth]);

  return (
    <div className="rotating-containers" ref={containerRef}>
      <div className="container4">
        <p>Container 1</p>
      </div>
      <div className="container4">
        <p>Container 2</p>
      </div>
      <div className="container4">
        <p>Container 3</p>
      </div>
      <div className="container4">
        <p>Container 4</p>
      </div>
      <div className="container4">
        <p>Container 5</p>
      </div>
    </div>
  );
};

export default RotatingContainers;
