import React, { useState, useEffect } from 'react';
import './RotatingContainers.css';

const RotatingContainers = () => {
  const [activeIndex, setActiveIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setActiveIndex((prevIndex) => (prevIndex + 1) % 5);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="rotating-containers">
      <div className={`container4 ${activeIndex === 0 ? 'active' : ''}`}>
        <p>Container 1</p>
      </div>
      <div className={`container4 ${activeIndex === 1 ? 'active' : ''}`}>
        <p>Container 2</p>
      </div>
      <div className={`container4 ${activeIndex === 2 ? 'active' : ''}`}>
        <p>Container 3</p>
      </div>
      <div className={`container4 ${activeIndex === 3 ? 'active' : ''}`}>
        <p>Container 4</p>
      </div>
      <div className={`container4 ${activeIndex === 4 ? 'active' : ''}`}>
        <p>Container 5</p>
      </div>
    </div>
  );
};

export default RotatingContainers;
