import React, { useEffect, useRef, useState } from 'react';
import './RotatingContainers.css';
import fs from 'fs';

const RotatingContainers = () => {
  const containerRef = useRef(null);
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    // Read the content from reviews.txt file
    fs.readFile('reviews.txt', 'utf-8', (err, data) => {
      if (err) {
        console.error(err);
      } else {
        const reviewsArray = data.split('\n').filter(review => review.trim() !== '');
        setReviews(reviewsArray);
      }
    });

    const container = containerRef.current;
    const scrollWidth = container.scrollWidth;
    let scrollLeft = 0;

    const scrollContainers = () => {
      scrollLeft += 0.05;
      if (scrollLeft >= scrollWidth) {
        scrollLeft = 0;
      }
      container.scrollLeft = scrollLeft;
      requestAnimationFrame(scrollContainers);
    };

    requestAnimationFrame(scrollContainers);

    return () => {
      cancelAnimationFrame(scrollContainers);
    };
  }, []);

  return (
    <div className="rotating-containers" ref={containerRef}>
      {reviews.map((review, index) => (
        <div className="container4" key={index}>
          <p>{review}</p>
        </div>
      ))}
    </div>
  );
};

export default RotatingContainers;
